import React from "react";
import { connect } from "react-redux";
import { inputName } from "../actions";
class inputPlayerName extends React.Component {
  constructor(props) {
    super(props);

    this.textInput1 = React.createRef();
    this.textInput2 = React.createRef();
  }

  componentDidMount() {}
  render() {
    return (
      <div>
        <form
          className="playerName"
          onSubmit={e => {
            e.preventDefault();
            this.props.inputName([
              this.textInput1.current.value,
              this.textInput2.current.value
            ]);
          }}
        >
          <input
            ref={this.textInput1}
            className="playerName__input"
            type="text"
            maxLength="10"
            defaultValue={this.props.game[0].player}
          />
          <input
            ref={this.textInput2}
            className="playerName__input"
            type="text"
            maxLength="10"
            defaultValue={this.props.game[1].player}
          />
          <button>Set Names</button>
        </form>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return { game: state.game };
};

export default connect(
  mapStateToProps,
  { inputName }
)(inputPlayerName);
