import React from "react";
import "./PlayerCard.css";
import ButtonPlayList from "./ButtonPlayList";
import CatPath from "./CatPath";

const PlayerCard = ({ name, points }) => {
  return (
    <div
      className="player-card"
      style={{
        backgroundColor:
          points.active === 1 ? "pink" : " rgba(248, 248, 248, 0.966)"
      }}
    >
      <h3>{name}</h3>
      <div className="player-card__score">score: {points.score}</div>
      <div className="player-card__catpath">
        <CatPath points={points} />
      </div>
      <div className="player-card__buttons">
        <ButtonPlayList points={points} />
      </div>
    </div>
  );
};

export default PlayerCard;
