import { combineReducers } from "redux";

const typeOfHouseReducer = (houseType = 7, action) => {
  if (action.type !== "TYPE_OF_HOUSE") {
    return houseType;
  }

  switch (action.payload) {
    case "Small House":
      return (houseType = 5);
      break;
    case "Middle House":
      return (houseType = 7);
      break;
    case "Big House":
      return (houseType = 9);
      break;
  }
};

export default combineReducers({
  typeOfHouse: typeOfHouseReducer
});
