import React from "react";
import { connect } from "react-redux";
import ButtonSet from "./ButtonSet";
import { newGame } from "../actions";

import "./InputPlayerName.css";

class inputPlayerName extends React.Component {
  constructor(props) {
    super(props);

    this.textInput1 = React.createRef();
    this.textInput2 = React.createRef();
  }

  onClick = () => {
    this.props.newGame({
      player: [this.textInput1.current.value, this.textInput2.current.value],
      house: this.props.typedHouse
    });
  };

  render() {
    return (
      <div>
        <form className="playerName" onSubmit={e => e.preventDefault()}>
          <input
            ref={this.textInput1}
            className="playerName__input"
            type="text"
            maxLength="13"
            defauplaceholder="Enter the name of keeper"
            spellCheck="false"
          />
          <input
            ref={this.textInput2}
            className="playerName__input"
            type="text"
            maxLength="13"
            defaultValue={this.props.game[1].player}
            spellCheck="false"
          />
        </form>
        <ButtonSet onClick={this.onClick} />
      </div>
    );
  }
}

const mapStateToProps = state => {
  return { typedHouse: state.typedHouse, game: state.game };
};

export default connect(
  mapStateToProps,
  { newGame }
)(inputPlayerName);
