import React from "react";
import { connect } from "react-redux";
import ButtonSet from "./ButtonSet";
import { inputName } from "../actions";
import "./InputPlayerName.css";

class inputPlayerName extends React.Component {
  constructor(props) {
    super(props);

    this.textInput1 = React.createRef();
    this.textInput2 = React.createRef();
  }

  handleOnSubmit = e => {
    e.preventDefault();
    this.props.inputName([
      this.textInput1.current.value,
      this.textInput2.current.value
    ]);
  };

  render() {
    return (
      <div>
        <form className="playerName" onSubmit={this.handleOnSubmit}>
          <input
            ref={this.textInput1}
            className="playerName__input"
            type="text"
            maxLength="13"
            defaultValue={this.props.game[0].player}
            spellCheck="false"
          />
          <input
            ref={this.textInput2}
            className="playerName__input"
            type="text"
            maxLength="13"
            defaultValue={this.props.game[1].player}
            spellCheck="false"
          />
        </form>
        <ButtonSet />
      </div>
    );
  }
}

const mapStateToProps = state => {
  return { game: state.game };
};

export default connect(
  mapStateToProps,
  { inputName }
)(inputPlayerName);
