import React from "react";
import ButtonPlay from "./ButtonPlay";

class ButtonPlayList extends React.component {
  playerScore() {
    const FoodBtnScore = Math.floor(Math.random() * 4) + 1;
    const CatnipBtnScore = Math.round(Math.random() * -6 + Math.random() * 8);
    if (this.points.score >= 0) {
      const score = this.props.points.score + FoodBtnScore;
      const paw = Math.floor(this.props.points.score / 5);
      const circle = this.props.points.circle - paw;
      return { score, paw, circle };
    }
  }

  renderList() {
    const buttonIcon = ["pizza", "beer"];
    return buttonIcon.map((cur, index) => (
      <ButtonPlay
        icon={cur}
        key={index}
        onClick={() => this.props.btnPlay(this.playerScore)}
      />
    ));
  }

  render() {
    return <div>{this.renderList()}</div>;
  }
}

const mapDispatchToProps = () => {
  return {
    btnPlay
  };
};

export default ButtonPlayList;
