import React from "react";

const CommentArea = ({ roundScore }) => {
  let content = "";
  const commentsBox = {
    bad: `Your cat hates this kind of catnip. He is about to come back on the roof and never back again...You loose ${roundScore} points`,
    medium: `This wasn't the worst. You get ${roundScore} points`,
    good: `Your cat love this catnip. It is totally out of world now. You get ${roundScore} points`,
    prevStep:
      "That's enough! Your cat is dissapointed of you. It takes 1 step back to give you some time for thinking over your behaviour",
    nextStep:
      "Your cat appreciates your effort and decides to get 1 step towards you. That's one small step for cat, but giant leap for mankind."
  };

  if (roundScore < -4) {
    content = `${commentsBox.bad} ${commentsBox.prevStep}`;
  } else if (roundScore < -4 && roundScore <= 0) {
    content = commentsBox.bad;
  } else if (roundScore > 0 && roundScore < 5) {
    content = commentsBox.medium;
  } else if (roundScore > 5)
    content = `${commentsBox.good} ${commentsBox.nextStep}`;

  return (
    <div>
      <p>{content}</p>
    </div>
  );
};

export default CommentArea;

