import { combineReducers } from "redux";

const typedHouseReducer = (
  houseHeight = 7,
  nameHouse = "MiddleHouse",
  action
) => {
  if (action.type !== "TYPE_OF_HOUSE") {
    return { houseHeight, nameHouse };
  }

  switch (action.payload) {
    case "Small House":
      return { houseHeight: 5, nameHouse: action.payload };
    case "Middle House":
      return { houseHeight: 7, nameHouse: action.payload };
    case "Big House":
      return { houseHeight: 9, nameHouse: action.payload };
  }
};

export default combineReducers({
  typedHouse: typedHouseReducer
});
