import React from "react";

const CommentArea = props => {
  let content = "";
  const commentsBox = {
    bad:
      "Your cat hates this kind of catnip. He is about to come back on the roof and never back again...You loose _ points",
    medium: "This wasn't the worst. You get _ points",
    good:
      "Your cat love this catnip. It is totally out of world now. You get _ points",
    prevStep:
      "That's enough! Your cat is dissapointed of you. It takes 1 step back to give you some time for thinking over your behaviour",
    nextStep:
      "Your cat appreciates your effort and decides to get 1 step towards you. That's one small step for cat, but giant leap for mankind."
  };

  if (points.roundScore < -4) {
    content = `${commentsBox.bad} ${commentsBox.prevStep}`;
  } else if (points.roundScore < -4 && points.roundScore <= 0) {
    content = commentsBox.bad;
  } else if (points.roundScore > 0 && points.roundScore < 5) {
    content = commentsBox.medium;
  } else content = `${commentsBox.good} ${commentsBox.nextStep}`;

  return (
    <div>
      <p>{content}</p>
    </div>
  );
};

export default CommentArea;

