import { combineReducers } from "redux";

const typedHouseReducer = (typedHouse = 7, action) => {
  if (action.type !== "TYPE_OF_HOUSE") {
    return typedHouse;
  }

  switch (action.payload) {
    case "small":
      return (typedHouse = 5);
    case "middle":
      return (typedHouse = 7);
    case "big":
      return (typedHouse = 9);
  }
};

const gameReducer = (
  initialState = [
    {
      player: "Player 1",
      score: 0,
      pathLength: 7,
      circleAmount: 0,
      pawAmount: 0,
      active: 1,
      playerStatus: ""
    },
    {
      player: "Player 2",
      score: 0,
      pathLength: 7,
      circleAmount: 0,
      pawAmount: 0,
      active: 0,
      playerStatus: ""
    }
  ],
  action
) => {
  switch (action.type) {
    case "NEW_GAME":
      return initialState.map(el => {
        el.score = 0;
        el.pathLength = action.payload;
        el.circleAmount = 0;
        el.pawAmount = 0;
        playerStatus = "";
        return el;
      });
    case "BTN_PLAY":
      return initialState.map(cur => {
        if (cur.active === 1) {
          cur.score = action.payload.score;
          cur.circleAmount = action.payload.circle;
          cur.pawAmount = action.payload.paw;
          cur.active = 0;
          cur.playerStatus = action.payload.playerStatus;
          return cur;
        } else {
          cur.active = 1;
          return cur;
        }
      });

    default:
      return initialState;
  }
};

export default combineReducers({
  typedHouse: typedHouseReducer,
  game: gameReducer
});
