import { combineReducers } from "redux";

const typedHouseReducer = (typedHouse = 7, action) => {
  if (action.type !== "TYPE_OF_HOUSE") {
    return typedHouse;
  }

  switch (action.payload) {
    case "small":
      return (typedHouse = 5);
    case "middle":
      return (typedHouse = 7);
    case "big":
      return (typedHouse = 9);
  }
};

const newGameReducer = (
  initialState = [
    { score0: 0, circleAmount: 0, pawAmount0: 0 },
    { score1: 0, circleAmount: 0, pawAmount1: 0 }
  ],
  action
) => {
  switch (action.type) {
    case "NEW_GAME":
      return initialState.map(el => {
        el.circleAmount = action.payload;
        return el;
      });

    default:
      return initialState;
  }
};

export default combineReducers({
  typedHouse: typedHouseReducer,
  newGame: newGameReducer
});
